contract StabilityFeeCollector is IStabilityFeeCollector, AccessControl, Pausable {
    using WadRayMath for uint256;

    // --- Data ---
    struct StabilityFeeInfo {
        uint256 ratioPerSecond; // Collateral-specific, per-second rate(stability fee in ledger) change ratio [ray]
        uint256 lastUpdatedAt; // Time of last drip [unix epoch time]
    }

    mapping(bytes32 => StabilityFeeInfo) public override stabilityFeeInfo;
    IAddressBook public addressBook;
    address public __legacy1;
    address public __legacy2;
    address public __legacy3;

    function __StabilityFeeCollector_init() external initializer {
        __Pausable_init();
    }

    // --- Administration ---
    function init(bytes32 collateralType) external onlyAdmin {
        StabilityFeeInfo storage info = stabilityFeeInfo[collateralType];
        require(info.ratioPerSecond == 0, "StabilityFeeUpdater::init: already init");
        info.ratioPerSecond = WadRayMath.RAY;
        info.lastUpdatedAt = block.timestamp;
    }

    function config(
        bytes32 collateralType,
        bytes32 what,
        uint256 data
    ) external onlyAdmin {
        if (what == "ratioPerSecond") stabilityFeeInfo[collateralType].ratioPerSecond = data;
        else revert("StabilityFeeUpdater::config: unrecognized-param");
    }

    function config(bytes32 what, address data) external onlyAdmin {
        if (what == "addressBook") addressBook = IAddressBook(data);
        else revert("StabilityFeeUpdater::config: unrecognized-param");
    }

    function updateRatioPerSecond(bytes32 collateralType) public override onlyOperator {
        IYieldManager yieldManager = IYieldManager(addressBook.yieldManager());
        uint256 yieldApr = yieldManager.getYieldApr(collateralType);
        uint256 targetApr = yieldManager.getTargetApr();
        if (targetApr > yieldApr) {
            uint256 clippedApr = SafeMath.min((WadRayMath.RAY * 2) / 10, (targetApr - yieldApr));
            stabilityFeeInfo[collateralType].ratioPerSecond = WadRayMath.RAY + clippedApr / 365 days;
        } else {
            stabilityFeeInfo[collateralType].ratioPerSecond = WadRayMath.RAY;
        }
    }

    function updateRatioPerSecond() external override onlyOperator {
        bytes32[] memory collateralTypeList = IConfigurator(addressBook.configurator()).collateralTypeList();
        uint256 length = collateralTypeList.length;
        for (uint256 i = 0; i < length; i++) {
            updateRatioPerSecond(collateralTypeList[i]);
        }
    }

    // --- Stability Fee Collection ---
    function updateRate(bytes32 collateralType) external override onlyAdmin {
        require(block.timestamp >= stabilityFeeInfo[collateralType].lastUpdatedAt, "StabilityFeeUpdater::updateRate: invalid time");
        require(stabilityFeeInfo[collateralType].ratioPerSecond > 0, "StabilityFeeUpdater::updateRate: invalid ratioPerSecond");
        ILedger ledger = ILedger(addressBook.ledger());
        IKSDGateway ksdGateway = IKSDGateway(addressBook.ksdGateway());

        (, , uint256 prevRate, , , ) = ledger.collateralInfo(collateralType);
        uint256 rate = getRateToUpdate(collateralType);
        ledger.collectStabilityFee(collateralType, address(this), SafeCast.toInt256(rate) - SafeCast.toInt256(prevRate));
        stabilityFeeInfo[collateralType].lastUpdatedAt = block.timestamp;
        emit UpdateRate(block.timestamp, collateralType, rate);

        uint256 ksdAmountInRay = ledger.ksd(address(this));
        uint256 ksdAmount = ksdAmountInRay / WadRayMath.RAY;
        ksdGateway.exit(address(this), ksdAmount);
        _distributeKsd(ksdAmount);
    }

    function getRateToUpdate(bytes32 collateralType) public view override returns (uint256 rate) {
        ILedger ledger = ILedger(addressBook.ledger());
        (, , uint256 prevRate, , , ) = ledger.collateralInfo(collateralType);
        rate = WadRayMath
            .rpow(stabilityFeeInfo[collateralType].ratioPerSecond, block.timestamp - stabilityFeeInfo[collateralType].lastUpdatedAt, WadRayMath.RAY)
            .rmul(prevRate);
    }

    function _distributeKsd(uint256 ksdAmount) internal {
        IKIP7 ksd = IKIP7(addressBook.ksd());
        IDistributor ksdRewardManager = IDistributor(addressBook.ksdRewardManager());

        ksd.approve(address(ksdRewardManager), ksdAmount);
        ksdRewardManager.distribute(ksdAmount);
    }
}
