/// @title Treasury fund.
/// @notice Aim to secure price stability of KSD through an active open market operation
///         Manages the account using the basic concepts of accountings (capital/debt/asset)
/// @dev Externally monitor the assets held by the treasury and call the appropriate function.
contract Treasury is ITreasury, AccessControl, Pausable {
    using WadRayMath for uint256;

    // --- data ---
    address public override ksd; //ksd token contract (kokoa stable token)
    address public override kusdt; //kusdt token contract
    address public override kdai; //kdai token contract

    mapping(address => bool) public override tokens; //Treasury tokens (ksd, kusdt, kdai)
    IKlaySwapBridge public klaySwapBridge; // klayswapbridge contract

    int256 public ksdDebt;
    uint256 public orderMaxRatio; // 6
    uint256 public ksdUpStrongLimit; // 120
    uint256 public ksdDownStrongLimit; // 90

    address public kusdc;
    address public oracle;

    uint256 public ksdUpStrongTarget; // 115
    uint256 public ksdUpWeakLimit; // 105
    uint256 public ksdUpWeakTarget; // 102
    uint256 public ksdDownStrongTarget; // 95
    uint256 public ksdDownWeakLimit; // 98
    uint256 public ksdDownWeakTarget; // 100

    function __Treasury_init(
        address ksd_,
        address kusdt_,
        address kdai_,
        address klaySwapBridge_
    ) public initializer {
        __Pausable_init();
        ksd = ksd_;
        kusdt = kusdt_;
        kdai = kdai_;
        tokens[ksd] = true;
        tokens[kusdt] = true;
        tokens[kdai] = true;
        klaySwapBridge = IKlaySwapBridge(klaySwapBridge_);
    }

    function config(bytes32 what, uint256 data) external onlyOwner {
        if (what == "orderMaxRatio") orderMaxRatio = data;
        else if (what == "ksdUpStrongLimit") ksdUpStrongLimit = data;
        else if (what == "ksdUpStrongTarget") ksdUpStrongTarget = data;
        else if (what == "ksdUpWeakLimit") ksdUpWeakLimit = data;
        else if (what == "ksdUpWeakTarget") ksdUpWeakTarget = data;
        else if (what == "ksdDownStrongLimit") ksdDownStrongLimit = data;
        else if (what == "ksdDownStrongTarget") ksdDownStrongTarget = data;
        else if (what == "ksdDownWeakLimit") ksdDownWeakLimit = data;
        else if (what == "ksdDownWeakTarget") ksdDownWeakTarget = data;
        else revert("Treasury::config: unrecognized-param");
    }

    function config(bytes32 what, address data) external onlyOwner {
        if (what == "oracle") oracle = data;
        else if (what == "kusdc") {
            kusdc = data;
            tokens[kusdc] = true;
        } else revert("Treasury::config: unrecognized-param");
    }

    /// @notice ksd price in wad uint
    function ksdStandardPrice() public view returns (uint256) {
        (IFeed klayFeed, ) = IOracle(oracle).collateralInfo(bytes32("klay"));
        (uint256 klayOraclePrice, bool valid) = klayFeed.getPrice();
        require(valid, "Treasury::ksdStandardPrice: klay oracle price should be valid");
        address lpPool = klaySwapBridge.tokenToPool(address(0), ksd);
        (uint256 klayAmount, uint256 ksdAmount) = IKlaytnExchange(lpPool).getCurrentPool();
        return (klayOraclePrice * klayAmount) / ksdAmount;
    }

    /// @dev x * y = k 에서, poolA 와 poolB 가 ratioA : ratioB 가 되려면 a 를 얼마만큼 스왑해야 하는지를 계산.
    function calcAmount(
        uint256 poolA,
        uint256 poolB,
        uint256 targetRatioA,
        uint256 targetRatioB
    ) public pure returns (uint256) {
        uint256 newPoolA = Math.sqrt((poolA * poolB * targetRatioA) / targetRatioB);
        return newPoolA - poolA;
    }

    function safeSellKsd() public override onlyOperator {
        uint256 ksdPrice = ksdStandardPrice();
        address[] memory stableTokenList = new address[](3);
        stableTokenList[0] = kdai;
        stableTokenList[1] = kusdt;
        stableTokenList[2] = kusdc;

        uint256 orderMax = (IKIP7(ksd).totalSupply() * orderMaxRatio) / 10000;
        for (uint256 i = 0; i < 3; i++) {
            address targetToken = stableTokenList[i];
            if (ksdPrice >= ((WadRayMath.WAD * ksdUpStrongLimit) / 100)) {
                _sellKsd(targetToken, ksdUpStrongTarget, type(uint256).max);
            } else if (ksdPrice >= ((WadRayMath.WAD * ksdUpWeakLimit) / 100)) {
                uint256 orderedAmount = _sellKsd(targetToken, ksdUpWeakTarget, orderMax);
                orderMax -= orderedAmount;
            }
        }
    }

    function _sellKsd(
        address targetToken,
        uint256 targetPrice,
        uint256 orderMax
    ) internal returns (uint256) {
        require(tokens[targetToken], "Treasury::_sellKsd: invalid targetToken");
        address lpPool = klaySwapBridge.tokenToPool(ksd, targetToken);
        (uint256 ksdAmountInPool, uint256 targetAmountInPool) = IKlaytnExchange(lpPool).getCurrentPool();

        uint256 diffDecimals = 0;
        {
            uint256 ksdDecimals = IKIP7Detailed(ksd).decimals();
            uint256 targetDecimals = IKIP7Detailed(targetToken).decimals();
            diffDecimals = ksdDecimals - targetDecimals; // ksd decimal이 항상 크다고 가정
        }
        uint256 ratioKsd = 100 * (10**diffDecimals);
        uint256 ratioTarget = targetPrice;

        uint256 ksdAmount = 0;
        if (ksdAmountInPool * ratioTarget < targetAmountInPool * ratioKsd) {
            ksdAmount = calcAmount(ksdAmountInPool, targetAmountInPool, ratioKsd, ratioTarget);
            ksdAmount = Math.min(orderMax, ksdAmount);
            ksdDebt += SafeCast.toInt256(ksdAmount);

            if (ksdAmount == 0) {
                return 0;
            }

            IKIP7Extended(ksd).mint(address(this), ksdAmount);
            IKIP7Extended(ksd).approve(address(klaySwapBridge), ksdAmount);
            klaySwapBridge.exchangeKctPos(ksd, ksdAmount, targetToken, 1, new address[](0));

            (ksdAmountInPool, targetAmountInPool) = IKlaytnExchange(lpPool).getCurrentPool();
            require(
                ksdAmountInPool * ratioTarget <= targetAmountInPool * ratioKsd,
                "Treasury::_sellKsd: ksd price should be greater than targetPrice"
            );
        }
        return ksdAmount;
    }

    function safeBuyKsd() public override onlyOperator {
        uint256 ksdPrice = ksdStandardPrice();
        address[] memory stableTokenList = new address[](3);
        stableTokenList[0] = kdai;
        stableTokenList[1] = kusdt;
        stableTokenList[2] = kusdc;

        for (uint256 i = 0; i < 3; i++) {
            address targetToken = stableTokenList[i];
            if (ksdPrice <= ((WadRayMath.WAD * ksdDownStrongLimit) / 100)) {
                _buyKsd(targetToken, ksdDownStrongTarget, type(uint256).max);
            } else if (ksdPrice <= ((WadRayMath.WAD * ksdDownWeakLimit) / 100)) {
                uint256 orderMax = (IKIP7(ksd).totalSupply() * orderMaxRatio) / 10000;
                _buyKsd(targetToken, ksdDownWeakTarget, orderMax);
            }
        }
    }

    function _buyKsd(
        address targetToken,
        uint256 targetPrice,
        uint256 orderMax
    ) internal {
        require(tokens[targetToken], "Treasury::_buyKsd: invalid targetToken");
        address lpPool = klaySwapBridge.tokenToPool(ksd, targetToken);
        (uint256 ksdAmountInPool, uint256 targetAmountInPool) = IKlaytnExchange(lpPool).getCurrentPool();

        uint256 diffDecimals = 0;
        {
            uint256 ksdDecimals = IKIP7Detailed(ksd).decimals();
            uint256 targetDecimals = IKIP7Detailed(targetToken).decimals();
            diffDecimals = ksdDecimals - targetDecimals; // ksd decimal이 항상 크다고 가정
        }
        uint256 ratioKsd = 100 * (10**diffDecimals);
        uint256 ratioTarget = targetPrice;

        if (ksdAmountInPool * ratioTarget > targetAmountInPool * ratioKsd) {
            uint256 targetAmount = calcAmount(targetAmountInPool, ksdAmountInPool, ratioTarget, ratioKsd);
            targetAmount = Math.min(targetAmount, orderMax / (10**diffDecimals));
            targetAmount = Math.min(targetAmount, IKIP7(targetToken).balanceOf(address(this)));

            if (targetAmount == 0) {
                return;
            }

            IKIP7Extended(targetToken).approve(address(klaySwapBridge), targetAmount);
            uint256 ksdBalance = IKIP7(ksd).balanceOf(address(this));
            klaySwapBridge.exchangeKctPos(targetToken, targetAmount, ksd, 1, new address[](0));
            uint256 burnKsdAmount = IKIP7(ksd).balanceOf(address(this)) - ksdBalance;

            ksdDebt -= SafeCast.toInt256(burnKsdAmount);
            IKIP7Extended(ksd).burn(address(this), burnKsdAmount);

            (ksdAmountInPool, targetAmountInPool) = IKlaytnExchange(lpPool).getCurrentPool();
            require(ksdAmountInPool * ratioTarget >= targetAmountInPool * ratioKsd, "Treasury::_buyKsd: ksd price should be less than targetPrice");
        }
    }

    function withdrawToken(address token, uint256 amount) external onlyAdmin {
        IKIP7(token).transfer(msg.sender, amount);
    }

    function burnKSD(uint256 amount) external onlyAdmin {
        require(ksdDebt > 0);
        require(uint256(ksdDebt) >= amount);
        IKIP7(ksd).transferFrom(msg.sender, address(this), amount);
        ksdDebt -= int256(amount);
    }

    /// @notice Fallback function.
    receive() external payable {}
}
