/// @title Management of strategies to operate the deposited klay.
/// @notice Each strategy can stake klay.
/// @dev Independent strategies are called yield.
contract YieldFactory is IYieldFactory, AccessControl, Pausable {
    // --- data ---
    uint256 public override totalStaked; // Total amount of klay deposited in YieldFactory.
    mapping(address => bool) private __legacy1; // 더이상 안씀
    address[] private __legacy2; // 더이상 안씀

    IKIP7 public aklay;
    IAKlayYield public aklayYield;

    function __YieldFactory_init() public initializer {
        __Pausable_init();
    }

    // --- config ---
    function config(bytes32 what, address data) external onlyAdmin whenNotPaused {
        if (what == "aklay") {
            aklay = IKIP7(data);
        } else if (what == "aklayYield") {
            aklayYield = IAKlayYield(data);
        } else revert("YieldFactory::config: unrecognized-param");
    }

    function defaultYieldList() internal view returns (address[] memory yieldList) {
        yieldList = new address[](1);
        yieldList[0] = address(aklayYield);
    }

    function sortedYieldList() public view returns (address[] memory yieldList) {
        yieldList = new address[](1);
        yieldList[0] = address(aklayYield);
        for (uint256 i = 0; i < yieldList.length; i++) {
            uint256 apr_i = IYield(yieldList[i]).expectedApr();
            for (uint256 j = i + 1; j < yieldList.length; j++) {
                uint256 apr_j = IYield(yieldList[j]).expectedApr();
                if (apr_i > apr_j) {
                    apr_i = apr_j;
                    address tmp = yieldList[j];
                    yieldList[j] = yieldList[i];
                    yieldList[i] = tmp;
                }
            }
        }
    }

    /// @notice Add klay to staking yield.
    /// @dev payable.
    function stake() external payable override onlyAdmin {
        address[] memory yieldList = sortedYieldList();
        address yield = yieldList[yieldList.length - 1];
        IYield(yield).stake{value: msg.value}();
        totalStaked = totalStaked + msg.value;
    }

    /// @notice Add Token to staking yield. (klay, aklay, ...)
    function stakeAKlay(uint256 amount) external override onlyAdmin {
        address[] memory yieldList = sortedYieldList();
        address yield = yieldList[yieldList.length - 1];
        aklay.transferFrom(msg.sender, address(this), amount);
        aklay.approve(yield, amount);
        IAKlayYield(yield).stakeAKlay(amount);
        totalStaked = totalStaked + amount;
    }

    /// @notice yieldFactory에서 aKLAY 등의 자산 인출할 수 있는 기능
    /// @dev 모든 yield에 대해서 동일한 비율 만킁의 자산을 인출
    /// @param to receiver.
    /// @param amount Amount of bklay to unstake(burn).
    function withdrawToken(address to, uint256 amount) external override onlyAdmin {
        require(totalStaked >= amount, "YieldFactory::withdrawToken:invalid unstaking amount");
        address[] memory yieldList = sortedYieldList();
        uint256 amountTotalUnstake;

        for (uint256 i = 0; i < yieldList.length; i++) {
            uint256 amountToUnstake = IYield(yieldList[i]).totalStaked() * amount / totalStaked;
            if (amountToUnstake == 0) continue;
            require(amountToUnstake <= IYield(yieldList[i]).totalStaked(), 'YieldFactory::withdrawToken:invalid unstaking amount');
            IYield(yieldList[i]).transferToken(to, amountToUnstake);
            amountTotalUnstake = amountTotalUnstake + amountToUnstake;
        }
        totalStaked = totalStaked - amountTotalUnstake;
    }

    /// @notice yieldFactory에서 aKLAY 등의 자산을 klay로 변환하여 인출할 수 있는 기능
    /// @dev 모든 yield에 대해서 동일한 비율 만킁의 자산을 인출
    /// @param to receiver.
    /// @param amount Amount of bklay to unstake(burn).
    function withdrawKlay(address to, uint256 amount) external override onlyAdmin {
        require(totalStaked >= amount, "YieldFactory::withdrawKlay:invalid unstaking amount");
        address[] memory yieldList = sortedYieldList();
        uint256 amountTotalUnstake;

        for (uint256 i = 0; i < yieldList.length; i++) {
            uint256 amountToUnstake = IYield(yieldList[i]).totalStaked() * amount / totalStaked;
            if (amountToUnstake == 0) continue;
            require(amountToUnstake <= IYield(yieldList[i]).totalStaked(), 'YieldFactory::withdrawKlay:invalid unstaking amount');
            IYield(yieldList[i]).transferKlay(to, amountToUnstake);
            amountTotalUnstake = amountTotalUnstake + amountToUnstake;
        }
        totalStaked = totalStaked - amountTotalUnstake;
    }


    /// @notice Remove klay from unstaking yield.
    /// @dev If the klay to unstake is insufficient for the yield, unstake from another yield.
    /// @param amount Amount of unstake klay for unstaking.
    function unstake(uint256 amount) external override onlyAdmin {
        require(totalStaked >= amount, "YieldFactory::unstake:invalid unstaking amount");
        address[] memory yieldList = sortedYieldList();
        totalStaked = totalStaked - amount;
        uint256 amountToUnstake = amount;
        for (uint256 i = 0; i < yieldList.length; i++) {
            if (amountToUnstake == 0) break;

            if (IYield(yieldList[i]).totalStaked() >= amountToUnstake) {
                IYield(yieldList[i]).unstake(amountToUnstake);
                amountToUnstake -= amountToUnstake;
            } else {
                uint256 _amount = IYield(yieldList[i]).totalStaked();
                IYield(yieldList[i]).unstake(_amount);
                amountToUnstake = amountToUnstake - _amount;
            }
        }
        require(amountToUnstake == 0, "YieldFactory::unstake: fail");
    }

    /// @notice Claim on every yield.
    function claim() external override {
        address[] memory yieldList = defaultYieldList();
        for (uint256 i = 0; i < yieldList.length; i++) {
            IYield(yieldList[i]).claim();
        }
    }

    /// @notice Claim on every yield.
    function pending() external view override returns (uint256 completed, uint256 yet) {
        completed = 0;
        yet = 0;
        address[] memory yieldList = defaultYieldList();
        for (uint256 i = 0; i < yieldList.length; i++) {
            (uint256 _completed, uint256 _yet) = IYield(yieldList[i]).pending();
            completed = completed + _completed;
            yet = yet + _yet;
        }
    }

    /// @notice In all yields, call applyProfit function.
    function applyProfitAll() external override {
        address[] memory yieldList = defaultYieldList();
        for (uint256 i = 0; i < yieldList.length; i++) {
            IYield(yieldList[i]).applyProfit(new address[](0));
        }
    }
