pragma solidity 0.8.3;

interface IYieldManager {
    struct YieldRecord {
        uint256 accYieldApr; // time weighted accumulative
        uint256 accEarnedKsdAmount;
        uint256 accRefundedKsdAmount;
        uint256 accStabilityFeeKsdAmount;
        uint256 accTargetKsdAmount; // exepct accumTargetKsdAmount == accumEarnedKsdAmount - accumRefundedKsdAmount + accumStabilityFeeKsdAmount
        uint256 totalLoan;
        uint256 rate;
        uint256 timestamp;
    }

    function yieldFactory(bytes32 collateralType) external view returns (address);

    function record(bytes32 collateralType, uint256 index)
        external
        view
        returns (
            uint256 accYieldApr,
            uint256 accEarnedKsdAmount,
            uint256 accRefundedKsdAmount,
            uint256 accStabilityFeeKsdAmount,
            uint256 accTargetKsdAmount,
            uint256 totalLoan,
            uint256 rate,
            uint256 timestamp
        );

    function recordLength(bytes32 collateralType) external view returns (uint256);

    function saveRecord(
        bytes32 collateralType,
        uint256 earnedKsdAmount,
        uint256 refundedKsdAmount,
        uint256 stabilityFeeKsdAmount,
        uint256 targetKsdAmount
    ) external;

    function getYieldApr(bytes32 collateralType) external view returns (uint256);

    function getTargetApr() external view returns (uint256);

    function manageProfit(bytes32 collateralType, uint256 earnedKsdAmount) external;
}
