pragma solidity 0.8.3;

import "../library/AccessControl.sol";
import "../library/Pausable.sol";
import "../library/openzeppelin/contracts/utils/Math.sol";
import "../library/WadRayMath.sol";
import "../interface/IStabilityFeeCollector.sol";
import "../interface/IYieldManager.sol";
import "../interface/ILedger.sol";
import "../interface/kip/IKIP7.sol";
import "../interface/IKsdRefundManager.sol";
import "../interface/IDistributor.sol";

contract YieldManager is IYieldManager, AccessControl, Pausable {
    using WadRayMath for uint256;

    mapping(bytes32 => address) public override yieldFactory;
    mapping(bytes32 => YieldRecord[]) public override record;
    mapping(bytes32 => uint256) public prevCursor;

    ILedger public ledger;
    IKIP7 public ksd;
    IStabilityFeeCollector public stabilityFeeCollector;
    IDistributor public ksdRewardManager;
    IKsdRefundManager public ksdRefundManager;

    function __YieldManager_init() public initializer {
        __Pausable_init();
    }

    function config(bytes32 what, address data) external onlyOwner {
        if (what == "ledger") ledger = ILedger(data);
        else if (what == "ksd") ksd = IKIP7(data);
        else if (what == "stabilityFeeCollector") stabilityFeeCollector = IStabilityFeeCollector(data);
        else if (what == "ksdRewardManager") ksdRewardManager = IDistributor(data);
        else if (what == "ksdRefundManager") ksdRefundManager = IKsdRefundManager(data);
        else revert("YieldManager::config:unrecognized-param");
    }

    function setYieldFactory(bytes32 collateralType, address yieldFactory_) external onlyAdmin {
        yieldFactory[collateralType] = yieldFactory_;
    }

    function recordLength(bytes32 collateralType) external view override returns (uint256) {
        return record[collateralType].length;
    }

    function saveRecord(
        bytes32 collateralType,
        uint256 earnedKsdAmount,
        uint256 refundedKsdAmount,
        uint256 stabilityFeeKsdAmount,
        uint256 targetKsdAmount
    ) public override onlyAdmin whenNotPaused {
        (, uint256 totalLoan, uint256 rate, , , ) = ledger.collateralInfo(collateralType);
        uint256 totalLoanedKsdAmount = (totalLoan * rate) / WadRayMath.RAY;
        if (totalLoanedKsdAmount <= 0) {
            return;
        }

        uint256 accEarnedKsdAmount = earnedKsdAmount;
        uint256 accRefundedKsdAmount = refundedKsdAmount;
        uint256 accStabilityFeeKsdAmount = stabilityFeeKsdAmount;
        uint256 accTargetKsdAmount = targetKsdAmount;
        uint256 accYieldApr = (earnedKsdAmount * WadRayMath.RAY * 365 days) / totalLoanedKsdAmount;

        YieldRecord[] storage recordList = record[collateralType];
        if (recordList.length > 0) {
            accYieldApr += recordList[recordList.length - 1].accYieldApr;
            accEarnedKsdAmount += recordList[recordList.length - 1].accEarnedKsdAmount;
            accRefundedKsdAmount += recordList[recordList.length - 1].accRefundedKsdAmount;
            accStabilityFeeKsdAmount += recordList[recordList.length - 1].accStabilityFeeKsdAmount;
            accTargetKsdAmount += recordList[recordList.length - 1].accTargetKsdAmount;
        }
        recordList.push(
            YieldRecord({
                accYieldApr: accYieldApr,
                accEarnedKsdAmount: accEarnedKsdAmount,
                accRefundedKsdAmount: accRefundedKsdAmount,
                accStabilityFeeKsdAmount: accStabilityFeeKsdAmount,
                accTargetKsdAmount: accTargetKsdAmount,
                totalLoan: totalLoan,
                rate: rate,
                timestamp: block.timestamp
            })
        );
        updateCursor(collateralType);
    }

    // ksd apr
    function updateCursor(bytes32 collateralType) public whenNotPaused {
        YieldRecord[] storage recordList = record[collateralType];
        uint256 length = recordList.length;
        if (length <= 1) {
            return;
        }
        YieldRecord memory curRecord = recordList[length - 1];
        uint256 _prevCursor = prevCursor[collateralType];

        uint256 i = _prevCursor;
        for (; i < _prevCursor + 100; i++) {
            if (recordList[i + 1].timestamp < curRecord.timestamp - 2 hours) {
                continue;
            }
            break;
        }
        prevCursor[collateralType] = i;
    }

    function getYieldApr(bytes32 collateralType) public view override returns (uint256) {
        YieldRecord[] storage recordList = record[collateralType];
        if (recordList.length <= 1) {
            return 0;
        }
        YieldRecord memory curRecord = recordList[recordList.length - 1];
        YieldRecord memory prevRecord = recordList[prevCursor[collateralType]];
        if (curRecord.timestamp <= 0 || prevRecord.timestamp <= 0) {
            return 0;
        }
        return (curRecord.accYieldApr - prevRecord.accYieldApr) / (curRecord.timestamp - prevRecord.timestamp);
    }

    function getTargetApr() public view override returns (uint256) {
        uint256 klayYieldApr = getYieldApr(bytes32("klay"));
        return klayYieldApr;
    }

    function manageProfit(bytes32 collateralType, uint256 earnedKsdAmount) external override onlyAdmin whenNotPaused {
        ksd.transferFrom(msg.sender, address(this), earnedKsdAmount);
        if (collateralType == bytes32("klay")) {
            ksd.approve(address(ksdRewardManager), earnedKsdAmount);
            ksdRewardManager.distribute(earnedKsdAmount);
            saveRecord(collateralType, earnedKsdAmount, 0, 0, earnedKsdAmount);
            return;
        }

        (, uint256 totalLoan, uint256 rate, , , ) = ledger.collateralInfo(collateralType);

        YieldRecord[] storage recordList = record[collateralType];
        if (recordList.length < 1) {
            ksd.approve(address(ksdRewardManager), earnedKsdAmount);
            ksdRewardManager.distribute(earnedKsdAmount);
            stabilityFeeCollector.updateRate(collateralType);
            saveRecord(collateralType, earnedKsdAmount, 0, 0, 0);
            return;
        }
        YieldRecord memory curRecord = recordList[recordList.length - 1];
        YieldRecord memory prevRecord = recordList[prevCursor[collateralType]];

        uint256 stabilityFeeKsdAmount = 0;
        uint256 targetKsdAmount = 0;
        uint256 ksdAmountToRefund = 0;
        {
            {
                uint256 elapsedTime = block.timestamp - curRecord.timestamp;
                uint256 loanedKsdAmount = curRecord.totalLoan.rayMul(curRecord.rate);
                uint256 nextRate = stabilityFeeCollector.getRateToUpdate(collateralType);
                stabilityFeeKsdAmount = totalLoan.rayMul(nextRate - rate);
                targetKsdAmount = (loanedKsdAmount.rayMul(getTargetApr()) * elapsedTime) / 365 days;
            }

            uint256 earnedKsdAmountForClosedInterval = curRecord.accEarnedKsdAmount - prevRecord.accEarnedKsdAmount + earnedKsdAmount;
            uint256 refundedKsdAmountForOpenInterval = curRecord.accRefundedKsdAmount - prevRecord.accRefundedKsdAmount; // wad
            uint256 stabilityFeeKsdAmountForClosedInterval = curRecord.accStabilityFeeKsdAmount +
                stabilityFeeKsdAmount -
                prevRecord.accStabilityFeeKsdAmount; // wad
            uint256 targetKsdAmountForClosedInterval = curRecord.accTargetKsdAmount + targetKsdAmount - prevRecord.accTargetKsdAmount; // wad

            uint256 gatheredKsdAmount = (earnedKsdAmountForClosedInterval +
                stabilityFeeKsdAmountForClosedInterval -
                refundedKsdAmountForOpenInterval);
            if (gatheredKsdAmount > targetKsdAmountForClosedInterval) {
                 ksdAmountToRefund = gatheredKsdAmount - targetKsdAmountForClosedInterval;
                 ksdAmountToRefund = Math.min(ksdAmountToRefund, earnedKsdAmount);
            }
        }

        ksd.approve(address(ksdRefundManager), ksdAmountToRefund);
        ksd.approve(address(ksdRewardManager), earnedKsdAmount - ksdAmountToRefund);
        ksdRefundManager.refund(collateralType, ksdAmountToRefund);
        ksdRewardManager.distribute(earnedKsdAmount - ksdAmountToRefund);
        stabilityFeeCollector.updateRate(collateralType);
        saveRecord(collateralType, earnedKsdAmount, ksdAmountToRefund, stabilityFeeKsdAmount, targetKsdAmount);
    }
}
